    private void btnMostrarDatosMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnMostrarDatosMouseClicked
        for (ElementoQuimico elementoQuimico : elementosQuimicos) {
            txtAreaMostrarDatos.setText(txtAreaMostrarDatos.getText()+"\n"+elementoQuimico);
        }

    }//GEN-LAST:event_btnMostrarDatosMouseClicked

